select distinct DistrictBranchID villageid,villagename,
b.blockid,b.blockname,
d.districtid,districtname,s.stateid,statename , c.countryid,c.CountryName
from m_districtbranchmapping dbm
inner join m_districtblock b on b.blockid=dbm.blockid
inner join m_district d on d.districtid=b.districtid
inner join m_state s on s.stateid=d.stateid
