 sum(D.AdjustedQuantity_FromDate) as AdjustedQuantity_FromDate ,
 sum(D.QuantityDispanced) as QuantityDispanced,
 sum(D.AdjustedQuantity_ToDate) as AdjustedQuantity_ToDate,
 sum(D.AdjustedQuantity_ToDate_Receipt) as AdjustedQuantity_ToDate_Receipt,
 sum(D.AdjustedQuantity_ToDate_Issue) as AdjustedQuantity_ToDate_Issue,
 ((sum(D.OpeningStock))+(sum(D.AdjustedQuantity_FromDate))-(sum(D.QuantityDispanced))+(sum(D.AdjustedQuantity_ToDate))) as ClosingStock

 
 from
 (select C.*,
 ITM.ItemName,
 FAC.FacilityName,
 ITMC.ItemCategoryName,
 
 sum(if(SAItm.IsAdded = 0b1, ifnull(SAItm.AdjustedQuantity,0),-(ifnull(SAItm.AdjustedQuantity,0))) ) as AdjustedQuantity_ToDate,
 sum(if(SAItm.IsAdded = 0b1, ifnull(SAItm.AdjustedQuantity,0),0) ) as AdjustedQuantity_ToDate_Receipt,
 sum(if(SAItm.IsAdded = 0b1, 0,(ifnull(SAItm.AdjustedQuantity,0))) ) as AdjustedQuantity_ToDate_Issue 
 
 from
 
 (select B.*,
 sum(ifnull(ISEx.Quantity,0)) as QuantityDispanced,
 
 (OpeningStock_temp) as OpeningStock 
 from
 
 (select A.*,
 sum(if(SAItm.IsAdded = 0b1, ifnull(SAItm.AdjustedQuantity,0),-(ifnull(SAItm.AdjustedQuantity,0))) ) as AdjustedQuantity_FromDate
 from
  
 (select
 ISE.Vanserialno,
 ISE.Vanid,
 ISE.ItemID,
 ISE.FacilityID,
 ISE.BatchNo,
 ifnull(ISE.Quantity,0) as TotalQuantityReceived,
 ISE.UnitCostPrice,
 ISE.ExpiryDate,
 ISE.EntryType,
 ISE.ProviderServiceMapID,
 ISE.CreatedDate as EntryDate,
 (ifnull(ISE.Quantity,0) - sum(ifnull(ISEx.Quantity,0))) as OpeningStock_temp
 FROM t_itemstockentry ISE
 left join t_itemstockexit ISEx
  on ISE.ItemStockEntryID = ISEx.ItemStockEntryID 
  and date(ISEx.CreatedDate) < v_FromDate
  
 where date(ISE.CreatedDate) <=   v_ToDate
 
 group by ISE.Vanserialno,ISE.Vanid
 ) A left join t_saitemmapping SAItm 
                 on SAItm.ItemStockEntryID=A.Vanserialno and A.vanid=SAItm.vanid
                 and date(SAItm.CreatedDate) <   v_FromDate
         
 group by A.Vanserialno,A.Vanid
  ) B left join t_itemstockexit ISEx 
                 on B.Vanserialno = ISEx.ItemStockEntryID   and B.vanid=ISEx.vanid
         and date(ISEx.CreatedDate) >=  v_FromDate        
         and date(ISEx.CreatedDate) <=   v_ToDate
         
  group by B.Vanserialno,B.Vanid
  ) C left join t_saitemmapping SAItm 
                 on SAItm.ItemStockEntryID=C.Vanserialno and C.Vanid=SAItm.vanid
                 and date(SAItm.CreatedDate) >=   v_FromDate
 				and date(SAItm.CreatedDate) <=   v_ToDate
         
         Inner Join m_Item ITM on C.ItemID = ITM.ItemID
         Inner Join m_Facility FAC on C.FacilityID = FAC.FacilityID
         Inner join m_ItemCategory ITMC on ITM.ItemCategoryID = ITMC.ItemCategoryID
         
 group by C.Vanserialno,C.vanid) D 
 where d.facilityid=ifnull(v_facilityid,d.facilityid)
 group by D.ItemID,d.facilityid,d.ItemCategoryName;
  
  
 
 END ;;
