CREATE DEFINER=`piramaldev`@`%` PROCEDURE `Pr_trend_overall_quality_rating`(v_psmrid int(11),
v_type varchar(50),v_month varchar(30))
begin
if(v_type='Cycle Wise') then
select cycle, sum(finalscore) Score from (
select t1.finalscore,
case when day(t1.createddate) between from_day and to_day then cycle else null end Cycle
from t_qualityauditorrating t1
inner join m_samplesize s on s.ProviderServiceMapID=t1.ProviderServiceMapID
where  month(t1.createddate)=month(date_sub(t1.createddate,interval 1 month)) and
t1.ProviderServiceMapID=ifnull(v_psmrid,t1.ProviderServiceMapID)
and year(t1.CreatedDate)=year(current_date())
)a where Cycle is not null
group by cycle;
end if;
if(v_type='year to date') then
select monthname(CreatedDate),sum(finalscore) Score from t_qualityauditorrating
where  ProviderServiceMapID=ifnull(v_psmrid,ProviderServiceMapID)
and year(createddate)=year(current_date())
group by monthname(CreatedDate);
end if;
if(v_type='Year Wise') then
select year(CreatedDate),sum(finalscore) Score from t_qualityauditorrating
 where year(createddate)>=year(date_sub(createddate,interval 5 year))
 -- and year(createddate)<>year(current_date())
 and ProviderServiceMapID=ifnull(v_psmrid,ProviderServiceMapID)
group by year(CreatedDate);
end if;
/*if(v_type='Month Wise'  and v_month is null) then
select monthname(CreatedDate),sum(finalscore) Score from t_qualityauditorrating
 where date(createddate)>=date_sub(createddate,interval 1 year) 
 and year(createddate)<>year(current_date())
 and ProviderServiceMapID=ifnull(v_psmrid,ProviderServiceMapID)
group by monthname(CreatedDate);
end if;*/
if(v_type='Month Wise' and v_month is not null) then
select monthname(CreatedDate),sum(finalscore) Score from t_qualityauditorrating
 where date(createddate)>=date(date_sub(createddate,interval 6 month) )
-- and year(createddate)<>year(current_date())
and monthname(CreatedDate)=v_month
 and ProviderServiceMapID=ifnull(v_psmrid,ProviderServiceMapID)
group by monthname(CreatedDate);
end if;
if(v_type='Quarter Wise') then
select case when Quarter(CreatedDate)=1 then 'Jan-March'
   when Quarter(CreatedDate)=2 then 'Apr-June'
   when Quarter(CreatedDate)=3 then 'July-Sep'
   when Quarter(CreatedDate)=4 then 'Oct-Dec' end Quartername
 ,sum(finalscore) Score from t_qualityauditorrating
 where year(createddate)>=year(date_sub(createddate,interval 1 year) )
 -- and year(createddate)<>year(current_date())
 and ProviderServiceMapID=ifnull(v_psmrid,ProviderServiceMapID)
group by case when Quarter(CreatedDate)=1 then 'Jan-March'
   when Quarter(CreatedDate)=2 then 'Apr-June'
   when Quarter(CreatedDate)=3 then 'July-Sep'
   when Quarter(CreatedDate)=4 then 'Oct-Dec' end ;
end if;
end ;;
